import React from "react";
import { Link } from "react-router-dom";

const Pagination = ({ coins, currentPage, setCurrentPage }) => {
  const previousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const nextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  const itemsPerPage = 10;

  const pageCount = Math.ceil(coins.length / itemsPerPage);

  const firstItem = currentPage * itemsPerPage;
  const lastItem = firstItem - itemsPerPage;

  console.log(firstItem);
  console.log(lastItem);

  return (
    <>
      <div className="flex">
        <Link
          to="#"
          className={`${
            currentPage === 1
              ? "flex items-center px-2 py-2 mr-1 text-gray-500 bg-white rounded-md shadow cursor-not-allowed dark:bg-gray-800 dark:text-gray-600"
              : "flex items-center px-2 py-2 mr-1 text-gray-700 transition-colors duration-200 transform bg-white rounded-md shadow dark:bg-gray-800 dark:text-gray-200 hover:bg-indigo-600 dark:hover:bg-indigo-500 hover:text-white dark:hover:text-gray-200"
          }`}
          onClick={previousPage}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            class="h-5 w-5"
            viewBox="0 0 20 20"
            fill="currentColor"
          >
            <path
              fill-rule="evenodd"
              d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z"
              clip-rule="evenodd"
            />
          </svg>
        </Link>

        <Link
          to="#"
          className="flex items-center px-2 py-2 ml-1 text-gray-700 transition-colors duration-200 transform bg-white rounded-md shadow dark:bg-gray-800 dark:text-gray-200 hover:bg-indigo-600 dark:hover:bg-indigo-500 hover:text-white dark:hover:text-gray-200"
          onClick={nextPage}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            class="h-5 w-5"
            viewBox="0 0 20 20"
            fill="currentColor"
          >
            <path
              fill-rule="evenodd"
              d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z"
              clip-rule="evenodd"
            />
          </svg>
        </Link>
      </div>
    </>
  );
};

export default Pagination;
