import React from "react";
import { Line } from "react-chartjs-2";

const LineChart = ({ chart }) => {
  /*   const ctx = canvas.getContext("2d");
  const gradient = ctx.createLinearGradient(0, 0, 0, 500);
  gradient.addColorStop(0, "rgba(34, 153, 84,0.4)");
  gradient.addColorStop(1, "rgba(34, 153, 84,0.01)"); // rgba color is required. */

  return (
    <>
      {chart.map((result, index) => {
        const coinPrice = [];

        for (let i = 0; i < result.history?.length; i += 1) {
          coinPrice.push(result.history[i]);
        }

        console.log(coinPrice);

        const data = {
          labels: [],
          datasets: [
            {
              data: coinPrice,
              fill: true,
              backgroundColor: "lightblue",
              borderColor: "#218c74",
              borderWidth: 3,
            },
          ],
        };

        const options = {
          scales: {
            y: {
              display: false,
            },
            x: {
              display: false,
            },
          },
          plugins: {
            legend: {
              display: false,
            },
          },
          radius: 0,
          tension: 0.4,
        };

        return (
          <>
            <h1>{result.slug}</h1>
            <Line key={index} data={data} options={options} />
          </>
        );
      })}
    </>
  );
};

export default LineChart;
