import React, { useState, useEffect } from "react";
import axios from "axios";
import { initializeApp } from "firebase/app";
import Dashboard from "./components/Dashboard";
import Login from "./components/Login";

const firebaseConfig = {
  apiKey: "AIzaSyCtPZPfcULkiAZPvpAcsccG_Bc_VvHqet4",
  authDomain: "crypto-dashboard-687d2.firebaseapp.com",
  projectId: "crypto-dashboard-687d2",
  storageBucket: "crypto-dashboard-687d2.appspot.com",
  messagingSenderId: "178147986762",
  appId: "1:178147986762:web:1194920ec4c2fae67b85b3",
};

// Initialize Firebase
initializeApp(firebaseConfig);

const App = () => {
  // states
  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState("");
  const [open, setOpen] = useState(false);
  const [user, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    const cryptoApiOptions = {
      method: "GET",
      url: "https://pro-api.coinmarketcap.com/v1/cryptocurrency/listings/latest",
      headers: {
        "X-CMC_PRO_API_KEY": process.env.REACT_APP_COINMARKETCAP_API_KEY,
      },
    };

    axios
      .request(cryptoApiOptions)
      .then(function (res) {
        var parsedData = JSON.parse(JSON.stringify(res));
        console.log(parsedData);
        console.log(parsedData.data.data);
        setCoins(parsedData.data.data);
      })
      .catch(function (err) {
        console.error(err);
      });

    /*      const cryptoNewsApiOptions = {
      method: "GET",
      url: "https://crypto-news5.p.rapidapi.com/",
      headers: {
        "x-rapidapi-host": "crypto-news5.p.rapidapi.com",
        "x-rapidapi-key": process.env.REACT_APP_CRYPTONEWS_API_KEY,
      },
    };

    axios
      .request(cryptoNewsApiOptions)
      .then(function (res) {
        console.log(res);
      })
      .catch(function (err) {
        console.error(err);
      }); */
  }, []);

  return (
    <>
      <div className="flex flex-col h-screen bg-gray-100">
        {/*        <Dashboard
          coins={coins}
          search={search}
          setSearch={setSearch}
          open={open}
          setOpen={setOpen}
        /> */}
        <Login
          user={user}
          setUser={setUser}
          email={email}
          setEmail={setEmail}
          password={password}
          setPassword={setPassword}
        />
      </div>
    </>
  );
};

export default App;
