import React from "react";
import { useParams } from "react-router-dom";

const CoinDetails = ({ coins }) => {
  const { id } = useParams();

  return (
    <>
      {coins
        .filter((coin) => coin.id === Number(id)) // seems useParams returned id as string instead of number.
        .map((coin, index) => (
          <div key={index}>
            <h1>CoinDetails: {coin.name}</h1>
            <p>{coin.marketCap}</p>
          </div>
        ))}
    </>
  );
};

export default CoinDetails;
