import React from "react";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";

const SignUp = ({
  user,
  setUser,
  email,
  setEmail,
  password,
  setPassword,
  err,
  setErr,
}) => {
  const url = "";

  const signUp = () => {
    const auth = getAuth();
    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        setUser(userCredential.user);
        console.log("Account created.");
      })
      .catch((err) => {
        // Error
        const errorMsg = err.message;
        console.log("Error encountered.");
        setErr(errorMsg);
      });
  };

  const handleSubmit = (e) => {
    if (user) {
      e.preventDefault();
      setEmail("");
      setPassword("");
    } else {
      setErr("");
    }
  };

  return (
    <>
      <div className="w-full max-w-sm p-6 m-auto bg-white rounded-md shadow-md dark:bg-gray-800">
        <h1 className="text-3xl font-semibold text-center text-gray-700 dark:text-white">
          Sign Up
        </h1>

        <form className="mt-6" onSubmit={handleSubmit}>
          <div>
            <label
              htmlFor="email"
              className="block text-sm text-gray-800 dark:text-gray-200"
            >
              Email
            </label>
            <input
              type="text"
              value={email}
              className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
              onChange={(e) => {
                setEmail(e.target.value);
              }}
            />
          </div>

          <div className="mt-4">
            <label
              htmlFor="password"
              className="block text-sm text-gray-800 dark:text-gray-200"
            >
              Password
            </label>
            <input
              type="password"
              value={password}
              className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border rounded-md dark:bg-gray-800 dark:text-gray-300 dark:border-gray-600 focus:border-blue-500 dark:focus:border-blue-500 focus:outline-none focus:ring"
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            />
            {err ? (
              <label className="text-xs text-red-500 ">{err}</label>
            ) : null}
          </div>

          <div className="mt-6">
            <button
              type="submit"
              className="w-full px-4 py-2 tracking-wide text-white transition-colors duration-200 transform bg-gray-700 rounded-md hover:bg-gray-600 focus:outline-none focus:bg-gray-600"
              onClick={signUp}
            >
              Sign Up
            </button>
          </div>
        </form>

        <p className="mt-8 text-xs font-light text-center text-gray-400">
          Already have an account?
          <a
            href={url}
            className="ml-1 font-medium text-gray-700 dark:text-gray-200 hover:underline"
          >
            Log In
          </a>
        </p>
      </div>
    </>
  );
};

export default SignUp;
