import React, { useState, useEffect } from "react";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { doc, getDoc } from "firebase/firestore";

// Firebase
import { db } from "./firebase/firebase.config";

// Components
import SignIn from "./pages/signinpage/SignIn";
import SignUp from "./pages/signuppage/SignUp";
import Sidebar from "./components/sidebar/Sidebar";
import NavBar from "./components/navbar/Navbar";
import HomePage from "./pages/homepage/Home";
import CoinDetails from "./pages/coininfopage/CoinInfo";
import Account from "./pages/accountpage/Account";

const App = () => {
  // states
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [token, setToken] = useState("");
  const [user, setUser] = useState("");
  const [isLogged, setIsLogged] = useState(false);
  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    setIsLogged(JSON.parse(window.localStorage.getItem("userToken")));
  }, []);

  useEffect(() => {
    if (token) {
      const getUsers = async () => {
        const docRef = doc(db, "users", token.uid);
        const docSnap = await getDoc(docRef);

        console.log(docSnap.data());
        setMember(docSnap.data());
      };

      getUsers();
    }
  }, [token]);

  useEffect(() => {
    window.localStorage.setItem("userToken", isLogged);

    // Coinranking API call
    const coinRankingOptions = {
      method: "GET",
      url: "https://coinranking1.p.rapidapi.com/coins",
      headers: {
        "x-rapidapi-host": "coinranking1.p.rapidapi.com",
        "x-rapidapi-key": process.env.REACT_APP_COINRANKING_API_KEY,
      },
    };

    axios
      .request(coinRankingOptions)
      .then(function (res) {
        const parsed = JSON.parse(JSON.stringify(res));
        /*         console.log(parsed.data.data.coins); */
        setCoins(parsed.data.data.coins);
      })
      .catch(function (err) {
        console.error(err);
      });
  }, [isLogged]);

  return (
    <>
      <Router basename={process.env.PUBLIC_URL}>
        <Switch>
          <Route path="/signup">
            <SignUp
              token={token}
              settoken={setToken}
              name={name}
              setName={setName}
              email={email}
              setEmail={setEmail}
              password={password}
              setPassword={setPassword}
              errorMessage={errorMessage}
              setErrorMessage={setErrorMessage}
            />
          </Route>

          {token && isLogged ? (
            <>
              <div className="flex flex-row">
                <Sidebar />
                <div className="flex flex-col w-full bg-gray-50 justify-items-center">
                  <NavBar
                    token={token}
                    settoken={setToken}
                    user={user}
                    coins={coins}
                    search={search}
                    setSearch={setSearch}
                    setIsLogged={setIsLogged}
                  />
                  <Switch>
                    <Route path="/dashboard">
                      <HomePage coins={coins} />
                    </Route>
                    <Route path="/cryptocurrencies/:uuid">
                      <CoinDetails coins={coins} />
                    </Route>
                    <Route path="/profile">
                      <Account
                        isLogged={isLogged}
                        setIsLogged={setIsLogged}
                        token={token}
                        settoken={setToken}
                        email={email}
                        setEmail={setEmail}
                        password={password}
                        setPassword={setPassword}
                        errorMessage={errorMessage}
                        setErrorMessage={setErrorMessage}
                      />
                    </Route>
                  </Switch>
                </div>
              </div>
            </>
          ) : (
            <Route path="/" exact>
              <SignIn
                isLogged={isLogged}
                setIsLogged={setIsLogged}
                token={token}
                settoken={setToken}
                email={email}
                setEmail={setEmail}
                password={password}
                setPassword={setPassword}
                errorMessage={errorMessage}
                setErrorMessage={setErrorMessage}
              />
            </Route>
          )}
        </Switch>
      </Router>
    </>
  );
};

export default App;
