import React from "react";
import { Line } from "react-chartjs-2";

const LineChart = ({ history }) => {
  const coinHistory = history.map((result) => result.history);

  const data = (canvas) => {
    const ctx = canvas.getContext("2d");
    const gradient = ctx.createLinearGradient(0, 0, 0, 500);
    gradient.addColorStop(0, "rgba(34, 153, 84,0.4)");
    gradient.addColorStop(1, "rgba(34, 153, 84,0.01)"); // rgba color is required.

    return {
      labels: ["", "", "", "", "", "", "", "", "", "", "", ""],
      datasets: [
        {
          fill: true,
          backgroundColor: gradient,
          borderColor: "#218c74",
          borderWidth: 3,
          data: [coinHistory],
        },
      ],
    };
  };

  const options = {
    plugins: {
      legend: {
        display: false,
      },
    },
    scales: {
      y: {
        display: false,
      },
      x: {
        display: false,
      },
    },
    radius: 0,
    tension: 0.4,
  };

  return (
    <>
      <Line data={data} options={options} />
    </>
  );
};

export default LineChart;
