import React from "react";
import { Line } from "react-chartjs-2";

const LineChart = ({ history }) => {
  /*   const ctx = canvas.getContext("2d");
  const gradient = ctx.createLinearGradient(0, 0, 0, 500);
  gradient.addColorStop(0, "rgba(34, 153, 84,0.4)");
  gradient.addColorStop(1, "rgba(34, 153, 84,0.01)"); // rgba color is required. */

  /*   console.log(history); */

  const coinHistory = history.map((result) => result.history);

  const eachCoinHistory = [];

  for (let i = 0; i < coinHistory.length; i++) {
    eachCoinHistory.push(coinHistory[i]);
  }

  const data = {
    labels: [
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
      "",
    ],
    datasets: [
      {
        data: eachCoinHistory,
        fill: true,
        backgroundColor: "lightblue",
        borderColor: "#218c74",
        borderWidth: 3,
      },
    ],
  };

  const options = {
    scales: {
      y: {
        display: false,
      },
      x: {
        display: false,
      },
    },
    plugins: {
      legend: {
        display: false,
      },
    },
    radius: 0,
    tension: 0.4,
  };

  return (
    <>
      <Line data={data} options={options} />
    </>
  );
};

export default LineChart;
